import React, { useState, useEffect } from 'react';
import { View, Text, TextInput } from 'react-native';

const AgeScreen = () => {
    const [ageInYears, setAgeInYears] = useState('');
    const [ageInWeeks, setAgeInWeeks] = useState('');
    const [ageInDays, setAgeInDays] = useState('');

    const calculateAge = (age) => {
        const years = parseInt(age);
        const weeks = years * 52;
        const days = years * 365;

        setAgeInYears(years.toString());
        setAgeInWeeks(weeks.toString());
        setAgeInDays(days.toString());
    };

    useEffect(() => {
        // Calculate the age when the component mounts
        calculateAge('');
    }, []);

    return (
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
            <Text>Age</Text>
            <Text>Age Calculator</Text>
            <TextInput
                placeholder="Age in years"
                value={ageInYears}
                onChangeText={(value) => calculateAge(value)}
                keyboardType="numeric"
                style={{
                    marginVertical: 10,
                    paddingHorizontal: 10,
                    width: 200,
                    height: 40,
                    borderColor: 'gray',
                    borderWidth: 1,
                }}
            />
            <TextInput
                placeholder="Age in weeks"
                value={ageInWeeks}
                onChangeText={setAgeInWeeks}
                keyboardType="numeric"
                style={{
                    marginVertical: 10,
                    paddingHorizontal: 10,
                    width: 200,
                    height: 40,
                    borderColor: 'gray',
                    borderWidth: 1,
                }}
            />
            <TextInput
                placeholder="Age in days"
                value={ageInDays}
                onChangeText={setAgeInDays}
                keyboardType="numeric"
                style={{
                    marginVertical: 10,
                    paddingHorizontal: 10,
                    width: 200,
                    height: 40,
                    borderColor: 'gray',
                    borderWidth: 1,
                }}
            />
        </View>
    );
};

export default AgeScreen;
