import React, { useState } from 'react';
import { View, Text, TextInput, Button } from 'react-native';

// Example of a global context variable to store the profile data
const ProfileContext = React.createContext();

const ProfileScreen = () => {
    const [name, setName] = useState('');
    const [age, setAge] = useState('');
    const [weight, setWeight] = useState('');
    const [height, setHeight] = useState('');

    const saveProfile = () => {
        // Save the profile data to the global context variable
        ProfileContext.profile = {
            name: name,
            age: age,
            weight: weight,
            height: height,
        };

        // Perform any other desired actions with the profile data

        // Log the profile data to the console for demonstration purposes
        console.log('Profile saved:', ProfileContext.profile);
    };

    return (
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
            <View style={{ alignItems: 'left', justifyContent: 'left' }}>
                <View style={{ flexDirection: 'row', alignItems: 'center', marginLeft: -10 }}>
                    <Text style={{ marginRight: 10 }}>Current Values:</Text>
                    <Text style={{ marginRight: 10 }}>Name: {name}</Text>
                    <Text style={{ marginRight: 10 }}>Age: {age}</Text>
                    <Text style={{ marginRight: 10 }}>Weight: {weight}</Text>
                    <Text>Height: {height}</Text>
                </View>
                <Text>Profile</Text>
                <View style={{ alignItems: 'center', justifyContent: 'center' }}>
                    {/* Remaining code unchanged */}
                </View>
                <Button title="Save Profile" onPress={saveProfile} />
            </View>
        </View>
    );
};

export default ProfileScreen;
